package Lecture1;

public class Operators_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a  = 101;
		int b = 20;
		
		int c = a+b;
		System.out.println(c);
		int d = a-b;
		System.out.println(d);
		int e = a*b;
		System.out.println(e);
		
		
		int f = a/b;
		System.out.println(f);
		
		
		int g =  a%b;
		System.out.println(g);
		
		
		
		
	}

}
